"use strict";
function addition(a, b) {
    return a + b;
}
function soustraction(a, b) {
    return a - b;
}
function multiplication(a, b) {
    return a * b;
}
function division(a, b) {
    return a / b;
}
console.log("Addition :", addition(5, 3));
console.log("Soustraction :", soustraction(10, 4));
console.log("Multiplication :", multiplication(6, 7));
console.log("Division :", division(20, 5));
console.log("Division par z√©ro :", division(10, 0));
